			/* Write ""Contact"" component */

			oComp.NewRecord ();
			oComp.SetCopySource  (iContactComp);
			// oComp.CopyFieldValue (""PARNR"", ""Integration Id"");
			// oComp.CopyFieldValue (""PARNR"", ""Employee Number"");

